import{useEffect,useState}from"react";import Axios from"axios";export default function useUserData(){const[userData,setUserData]=useState({name:"",email:""}),storedUserId=sessionStorage.getItem("userId");return useEffect(()=>{const fetchUserData=async()=>{try{const response=await Axios.get(`http://localhost:8002/user/${storedUserId}`),users=response.data;if(users.length>0){const user=users[0];setUserData({name:user.name,email:user.email})}}catch(error){console.error("Error fetching user data:",error)}};storedUserId&&fetchUserData()},[storedUserId]),userData}